AddCSLuaFile()SWEP.Base			= "gmdm_base"SWEP.PrintName		= "WARE Projectile Crowbar"SWEP.Slot			= 1SWEP.SlotPos		= 0SWEP.DrawAmmo		= falseSWEP.DrawCrosshair	= trueSWEP.ViewModel		= "models/weapons/v_crowbar.mdl"		SWEP.WorldModel		= "models/weapons/w_crowbar.mdl"SWEP.HoldType = "pistol"SWEP.ShootSound = Sound ("weapons/slam/throw.wav")SWEP.RunArmAngle  = Angle( 70, 0, 0 )SWEP.RunArmOffset = Vector( 25, 4, 0 )SWEP.Delay = 0.3SWEP.TickDelay = 0.1SWEP.ProjectileEntity = "ware_crowbar_w"SWEP.ProjectileForce = 100000SWEP.Primary.ClipSize		= -1SWEP.Primary.DefaultClip	= -1SWEP.Primary.Automatic		= falseSWEP.Primary.Ammo			= "none"function SWEP:PrimaryAttack()	self.Weapon:SetNextPrimaryFire( CurTime() + self.Delay )	self.Weapon:SetNextSecondaryFire( CurTime() + self.TickDelay )		if ( !self:CanShootWeapon() ) then return end		self.Weapon:EmitSound( self.ShootSound )		if ( SERVER ) then			self:ThrowCrowbar( self.ProjectileForce, self.Owner )		self.Owner:StripWeapon( "ware_crowbar" )	endendif ( SERVER ) then		function SWEP:ThrowCrowbar( shotPower )				local owner = self.Owner				if ( !IsValid( owner ) ) then return end				local tr 	= owner:GetEyeTrace()		local ent 	= ents.Create( self.ProjectileEntity )			local forward = owner:EyeAngles():Forward()			ent:SetPos( owner:GetShootPos() + forward *16 )			ent:SetAngles( owner:EyeAngles() )					ent:SetOwner( owner )			ent:Spawn()				ent:Trail( "trails/physbeam.vmt", Color( 255, 255, 255, 90 ), 0.7, 1.2, 0.4 )				local phys = ent:GetPhysicsObject()			phys:ApplyForceCenter( owner:GetAimVector():GetNormalized() * shotPower )	end	endSWEP.Secondary.ClipSize 	= -1SWEP.Secondary.DefaultClip 	= -1SWEP.Secondary.Automatic 	= falseSWEP.Secondary.Ammo 		= "none"function SWEP:SecondaryAttack()end